import{global}from"/dist/scripts/global.js";import{helpers}from"/dist/scripts/global-helpers.js";const yearToken="[[year]]",curYear=(new Date).getFullYear();let data,template;function buildFooter(){global.getGlobalData().then(e=>{data={socialIcons:e.socialIcons,copyright:e.copyright.replace(yearToken,curYear),email:e.email},data.socialIcons.standard.push({name:"email",cssClass:"envelope",url:`mailto:${e.email}`}),template=Handlebars.templates[global.templateSources.footer](data),global.els.footer.insertAdjacentHTML("afterbegin",template)}).catch(e=>console.warn(e))}const footer={buildFooter:buildFooter};export{footer};